pragma solidity ^0.8.19;

import {ERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";

error TransferLock();
error RefundNotAvailable();

contract CrowdFund is ERC20{
    uint public endTime;
    uint public fundingGoal;

    mapping(address=>uint) public contributions;

    constructor(uint _endTime, uint _fundingGoal) ERC20("MyToken","MTKN"){
        endTime = _endTime;
        fundingGoal = _fundingGoal;
    }

    receive() payable external{
        if(msg.value != 0){
            _mint(msg.sender, msg.value);
            contributions[msg.sender] += msg.value;
        }
    }

    function _update(address from, address to, uint256 value) internal override{
        if(from != address(0) && (block.timestamp < endTime || address(this).balance < fundingGoal)){
            revert TransferLock();
        }
        super._update(from,to,value);
    }

    function claim() external returns(uint claimedAmount){
        if(block.timestamp < endTime){
            revert RefundNotAvailable();
        }
        if(address(this).balance >= fundingGoal){
            revert RefundNotAvailable();
        }
        claimedAmount = contributions[msg.sender];
        payable(msg.sender).transfer(claimedAmount);
    }
}
